targets.expr:
(
  max by (instance, akka_address, cluster_role, service_version, status) (
    max_over_time(
      akka_cluster_members{
        job=~"$job",
        instance=~"$instance",
        status=~"(Up|WeaklyUp|Joining|Leaving|Exiting)"
      }[5m]
    )
  ) > 0
)

(
  sum by (instance) (
    akka_actor_live_actors{job=~"$job", instance=~"$instance"}
  )
  and on (instance)
  max by (instance) (
    max_over_time(akka_cluster_members{job=~"$job", instance=~"$instance", status=~"(Up|WeaklyUp|Joining|Leaving|Exiting)"}[$__rate_interval])
  )
)
or on (instance)
(
  0 * max by (instance) (
    max_over_time(akka_cluster_members{job=~"$job", instance=~"$instance", status=~"(Up|WeaklyUp|Joining|Leaving|Exiting)"}[$__rate_interval])
  )
)

